mapper.select:
SELECT

FROM t_sys_operatation_log

   and log_id = #{logId} 
   and scope_name = #{scopeName} 
   and scope = #{scope} 
   and date_loged = #{dateLoged} 
   and method_name = #{methodName} 
   and data_id = #{dataId} 
   and type = #{type} 
   and user = #{user} 
   and user_name = #{userName} 
   and user_account = #{userAccount} 
   and ip = #{ip} 
   and operation_desc = #{operationDesc} 
   and millions_taken = #{millionsTaken} 
   and params = #{params} 



    order by ${order} ${orderDirection}


    order by log_id asc



  limit #{offset}, #{limit}

