mapper.update:
UPDATE t_sys_menu
SET STATUS = #{status,jdbcType=INTEGER}
WHERE ID = #{id,jdbcType=INTEGER}

mapper.select:
SELECT MENU_LEVEL
FROM t_sys_menu
WHERE ID = #{id,jdbcType=INTEGER}

SELECT MENU_NAME
FROM t_sys_menu
WHERE ID = #{id,jdbcType=INTEGER}

SELECT

FROM t_sys_menu

   and ID = #{id} 
   and MENU_NAME = #{menuName} 
   and MENU_URL = #{menuUrl} 
   and SORT = #{sort} 
   and PARENT_MENU_ID = #{parentMenuId} 
   and MENU_LEVEL = #{menuLevel} 
   and CREATE_TIME = #{createTime} 
   and STATUS = #{status} 
   and STYLE = #{style} 



    order by ${order} ${orderDirection}


    order by SORT asc



  limit #{offset}, #{limit}
