mapper.update:
UPDATE t_sys_user
SET STATUS = #{status,jdbcType=INTEGER}
WHERE ID = #{id,jdbcType=INTEGER}

UPDATE t_sys_user
SET STATUS = #{status,jdbcType=INTEGER}
WHERE ID in

  #{id}

mapper.select:
SELECT
COUNT(1)
FROM t_sys_user

   and ID = #{id} 
   and USER_NAME = #{userName} 
   and USER_PWD = #{userPwd} 
   and USER_TEL = #{userTel} 
   and USER_EMAIL = #{userEmail} 
   and USER_REAL_NAME = #{userRealName} 
   and REMARK = #{remark} 
   and CREATE_USER = #{createUser} 
   and CREATE_TIME = #{createTime} 
   and STATUS = #{status}

SELECT


    ID,


    USER_NAME,


    USER_PWD,


    USER_TEL,


    USER_EMAIL,


    USER_REAL_NAME,


    REMARK,


    CREATE_USER,


    CREATE_TIME,


    STATUS,


FROM t_sys_user

   and ID = #{id} 
   and USER_NAME = #{userName} 
   and USER_PWD = #{userPwd} 
   and USER_TEL = #{userTel} 
   and USER_EMAIL = #{userEmail} 
   and USER_REAL_NAME = #{userRealName} 
   and REMARK = #{remark} 
   and CREATE_USER = #{createUser} 
   and CREATE_TIME = #{createTime} 
   and STATUS = #{status} 



    order by ${order} ${orderDirection}


    order by ID asc



  limit #{offset}, #{limit}
