mapper.update:
UPDATE t_sys_user
SET STATUS = #{status,jdbcType=INTEGER}
WHERE ID = #{id,jdbcType=INTEGER}

UPDATE t_sys_user
SET STATUS = #{status,jdbcType=INTEGER}
WHERE ID in

  #{id}

mapper.select:
SELECT
COUNT(1)
FROM t_sys_user

   and ID = #{id} 
   and USER_NAME = #{userName} 
   and USER_PWD = #{userPwd} 
   and USER_TEL = #{userTel} 
   and USER_EMAIL = #{userEmail} 
   and USER_REAL_NAME = #{userRealName} 
   and REMARK = #{remark} 
   and CREATE_USER = #{createUser} 
   and CREATE_TIME = #{createTime} 
   and STATUS = #{status} 
   and ROLE_ID = #{roleId}

SELECT
tsu.ID, tsu.USER_NAME, tsu.USER_PWD, tsu.USER_TEL, tsu.USER_EMAIL, tsu.USER_REAL_NAME, tsu.REMARK, tsu.CREATE_USER,
tsu.CREATE_TIME, tsu.STATUS, tsu.ROLE_ID

  ,tsr.ROLE_DESCRIBE

FROM t_sys_user tsu

  left join t_sys_role tsr on tsu.ROLE_ID = tsr.ID


   and tsu.ID = #{id} 
   and tsu.USER_NAME = #{userName} 
   and tsu.USER_PWD = #{userPwd} 
   and tsu.USER_TEL = #{userTel} 
   and tsu.USER_EMAIL = #{userEmail} 
   and tsu.USER_REAL_NAME = #{userRealName} 
   and tsu.REMARK = #{remark} 
   and tsu.CREATE_USER = #{createUser} 
   and tsu.CREATE_TIME = #{createTime} 
   and tsu.STATUS = #{status} 
   and tsu.ROLE_ID = #{roleId} 



    order by tsu.${order} ${orderDirection}


    order by tsu.ID asc



  limit #{offset}, #{limit}
